function slideshow() {
  // clone
  $('.slider-fc-1').clone().removeClass('slider-fc-1').addClass('slider-fc-2').insertAfter($('.slider-fc'));

  // set first
  $('.slider-fc-1').slick({
    draggable: false,
    dots: false,
    infinite: true,
    responsive: true,
    asNavFor: '.slider-2',
    touchThreshold: 20,
    speed: 1000,
    fade: true
  });

  // set second
  $('.slider-2').slick({
    dots: true,
    infinite: true,
    responsive: true,
    asNavFor: '.slider-fc-1',
    arrows: false,
    speed: 1000,
    easing: 'easeInOutQuart'
  });
}

$(function() {
  slideshow();
  setTimeout(function() {
    $('.slider-fc-1 .slick-next').click();
  }, 1000);
})


